mapper.select:
SELECT 
FROM QRTZ_TRIGGER_LOG  t


		AND t.job_group = #{jobGroup}


		AND t.job_id = #{jobId}


		AND t.trigger_time  >=  #{triggerTimeStart}


		AND t.trigger_time  <=  #{triggerTimeEnd}


		AND t.handle_code = 200


		AND (
			(t.trigger_code  >  0 AND t.trigger_code!=200) ||
			(t.handle_code  >  0 AND t.handle_code!=200)
		)


		AND (t.trigger_code = 200 AND t.handle_code=0)


ORDER BY id DESC
LIMIT #{offset}, #{pagesize}

SELECT 
FROM(
	SELECT qtl.*,rownum num
	FROM QRTZ_TRIGGER_LOG  qtl


			AND qtl.job_group = #{jobGroup}


			AND qtl.job_id = #{jobId}


			AND qtl.trigger_time  >=  #{triggerTimeStart}


			AND qtl.trigger_time  <=  #{triggerTimeEnd}


			AND qtl.handle_code = 200


			AND (
				(qtl.trigger_code  >  0 AND qtl.trigger_code!=200) ||
				(qtl.handle_code  >  0 AND qtl.handle_code!=200)
			)


			AND (qtl.trigger_code = 200 AND qtl.handle_code=0)


	ORDER BY qtl.id DESC
)t
where t.num between #{offset}*#{pagesize} and (#{offset}+1)*#{pagesize}

SELECT count(1)
FROM QRTZ_TRIGGER_LOG  t


		AND t.job_group = #{jobGroup}


		AND t.job_id = #{jobId}


		AND t.trigger_time  >=  #{triggerTimeStart}


		AND t.trigger_time  <=  #{triggerTimeEnd}


		AND t.handle_code = 200


		AND (
		(t.trigger_code  >  0 AND t.trigger_code!=200) ||
		(t.handle_code  >  0 AND t.handle_code!=200)
		)


		AND (t.trigger_code = 200 AND t.handle_code=0)

SELECT 
FROM QRTZ_TRIGGER_LOG  t
WHERE t.id = #{id}

mapper.insert:
select XXL_JOB_SEQ.nextval from dual
INSERT INTO QRTZ_TRIGGER_LOG (
	id,
	job_group,
	job_id
) VALUES (
	#{id},
	#{jobGroup},
	#{jobId}
)

mapper.update:
UPDATE QRTZ_TRIGGER_LOG
SET 
	handle_time= #{handleTime}, 
	handle_code= #{handleCode},
	handle_msg= #{handleMsg} 
WHERE id= #{id}

mapper.select:
SELECT count(1)
FROM QRTZ_TRIGGER_LOG  t


		AND t.handle_code = #{handleCode}

mapper.delete:
delete from QRTZ_TRIGGER_LOG


		AND job_group = #{jobGroup}


		AND job_id = #{jobId}


		AND trigger_time  <=  #{clearBeforeTime}


		AND id NOT in(
			SELECT id FROM(
				SELECT id FROM QRTZ_TRIGGER_LOG  t


						AND t.job_group = #{jobGroup}


						AND t.job_id = #{jobId}


				ORDER BY t.trigger_time desc
				LIMIT 0, #{clearBeforeNum}
			) t1
		)
